css:
#triangle {
  position: absolute;

  width: 0;
  height: 0;

  border-right: 40px solid #1c649c;
  border-bottom: 40px solid transparent;

  margin-left: 650px;
}

#watermark {
    /* the image you want to 'watermark' */
    height: 156px; /* or whatever, equal to the image you want 'watermarked' */
    width: 406px; /* as above */
    /* background-image: url(https://www.google.co.in/images/srpr/logo11w.png); */
    background-image: url(https://upload.wikimedia.org/wikipedia/commons/f/ff/DRAFT_ICON.png);
    background-position: 10 10;
    background-repeat: no-repeat;
    position: relative;
}

.title-font {
  color: #1c649c;
  font-size: 46px;
  margin-top: -20px;
  font-family: 'Brandon Grotesque Regular'
}

.border-right {
  border-right: 2px solid #f3f3f3;
}

.border-left {
