}

.border-top {
  border-top: 2px solid #f3f3f3;
}


.border-bt-2 {
  border-bottom: 2px solid #1c649c;
}

.border-bt-3 {
  border-bottom: 3px solid #1c649c;
}

p {
  margin: 0px !important;
}


.pl-2 {
  padding-left: 0.5rem;
}


h6 {
  font-family: "Raleway";
